scene1.init = function (){

     this.tileSet.src = this.tileSetName;
	 scene1.numTilesWidth = Math.floor(scene1.map.tilesets[0].imagewidth/scene1.map.tilesets[0].tilewidth);
	 scene1.numTilesHeight = Math.floor(scene1.map.tilesets[0].imageheight/scene1.map.tilesets[0].tileheight);
     scene1.arrayTiles = new Array();

    //Inicializamos un array con los datos de cada tile.
    for (var i2 = scene1.numTilesHeight - 1; i2 >= 0; i2--) {
    	for (var j2 = scene1.numTilesWidth - 1; j2 >= 0; j2--) {
    		scene1.arrayTiles[(i2*scene1.numTilesWidth) + j2] = [j2*24,i2*24];
    	}
    }

}


scene1.draw = function(){

		var tileValue;
		for (var i = this.map.height - 1; i >= 0; i--) {
			for (var j = this.map.width - 1; j >= 0; j--) {
				tileValue =  this.map.layers[0].data[(i*this.map.width) + j];
				if (tileValue > 0) {
				ctx.drawImage(this.tileSet, this.arrayTiles[tileValue-1][0], this.arrayTiles[tileValue-1][1], 24, 24, (j*24)- this.mapOffset.x, (i*24), 24, 24);
				}
			}
		}
    player1.draw();

}

scene1.update = function(){


   if(this.mapOffset.x < 1600){
   this.mapOffset.x += this.mapVelocity;

   }else{
       this.mapVelocity = 0;
   }
   player1.update();
}

